mapper.select:
select p_id, g_id, animal, birth, kind, breed, pet.profile_num, feature, gender, neutering, state,
        b.profile_name, b.profile_origin
from pet inner join img_profile b on pet.profile_num = b.profile_num where p_id=#{p_id}

mapper.update:
update pet a join img_profile b on a.profile_num=b.profile_num
    set animal=#{animal}, birth=#{birth}, kind=#{kind}, breed=#{breed},
        feature=#{feature}, gender=#{gender}, neutering=#{neutering},
               profile_name=#{profile_name} profile_origin=#{profile_origin}
where p_id=#{p_id}
